      &amp;lt;!-- 上拉加载更多 --&amp;gt;
      &amp;lt;van-list v-model=&amp;quot;loading&amp;quot; :finished=&amp;quot;finished&amp;quot; finished-text=&amp;quot;没有更多了&amp;quot; @load=&amp;quot;onLoad&amp;quot; :immediate-check=&amp;quot;false&amp;quot;&amp;gt;
        &amp;lt;!-- 循环渲染文章的列表 --&amp;gt;
